Statements.Statement:
/* BoardMapper.AddBoard */

INSERT INTO CO_BOARD (
  BOARD_NAME,
  BOARD_CONTENT,
  BOARD_TYPE_CODE,
  BOARD_ORDER,
  MAIN_SHOW_YN,
  USE_YN,
  CREATER_ID
)
VALUES (
  @BoardName,
  @BoardContent,
  @BoardTypeCode,
  IFNULL(@BoardOrder, (
    SELECT IFNULL(MAX(B.BOARD_ORDER), 0) + 1
    FROM CO_BOARD B
  )),
  @MainShowYn,
  @UseYn,
  @CreaterId
)
;SELECT LAST_INSERT_ID()
