Statements.Statement:
/* CodeMapper.AddCode */

INSERT INTO CO_CODE (
  CODE_ID,
  UP_CODE_ID,
  CODE_VALUE,
  CODE_NAME,
  CODE_CONTENT,
  CODE_ORDER,
  CODE_DEPTH,
  USE_YN,
  USER_ID,
  CREATER_ID
)
VALUES (
  @CodeId,
  @UpCodeId,
  @CodeValue,
  @CodeName,
  @CodeContent,
  IFNULL(@CodeOrder, (
    SELECT IFNULL(MAX(C.CODE_ORDER), 0) + 1
    FROM CO_CODE C
    WHERE 1=1

      AND C.UP_CODE_ID IS NULL


      AND C.UP_CODE_ID = @UpCodeId

  )),
  @CodeDepth,
  @UseYn,
  @UserId,
  @CreaterId
)
;SELECT @CodeId

/* CodeMapper.UpdateCode */
UPDATE CO_CODE SET CODE_ID = @CodeId, UP_CODE_ID = @UpCodeId, CODE_VALUE = @CodeValue, CODE_NAME = @CodeName, CODE_CONTENT = @CodeContent, CODE_ORDER = IFNULL(@CodeOrder, ( SELECT IFNULL(MAX(C.CODE_ORDER), 0) + 1 FROM CO_CODE C WHERE C.CODE_ID != @CodeId
AND C.UP_CODE_ID IS NULL
AND C.UP_CODE_ID = @UpCodeId
)), CODE_DEPTH = @CodeDepth, USE_YN = @UseYn, UPDATER_ID = @UpdaterId, UPDATE_DT = SYSDATE() WHERE CODE_ID = @OriginalCodeId

/* CodeMapper.RemoveCode */

DELETE FROM CO_CODE
WHERE CODE_ID = @CodeId
