  REGISTRATION_NO,
  CORPORATE_NAME,
  COMPANY_NAME,
  APPLY_CONTENT,
  APPLY_STATE_CODE,
  APPLICANT_ID,
  CREATER_ID
)
VALUES (
  REPLACE(@RegistrationNo, '-', ''),
  @CorporateName,
  @CompanyName,
  @ApplyContent,
  @ApplyStateCode,
  @CreaterId,
  @CreaterId
)
;SELECT LAST_INSERT_ID()
