Statements.Statement:
/* EmployeeRepository.AddEmployee */

INSERT INTO HM_EMPLOYEE (
  USER_ID,
  EMPLOYEE_NAME,
  GENDER_CODE,
  BIRTH_YMD,
  PHONE_NO,
  EMAIL_ADDR,
  CREATER_ID
)
VALUES (
  @UserId,
  @EmployeeName,
  @GenderCode,
  @BirthYmd,
  REPLACE(@PhoneNo, '-', ''),
  @EmailAddr,
  @CreaterId
)
;SELECT LAST_INSERT_ID()

/* EmployeeRepository.CountEmployeeEmailAddr */
SELECT COUNT(1) FROM HM_EMPLOYEE E WHERE E.EMAIL_ADDR = @EmailAddr
AND E.EMPLOYEE_ID != @EmployeeId
