Statements.Statement:
/* NotificationRepository.CountNotification */
SELECT COUNT(1) AS NOTIFICATION_COUNT FROM CO_NOTIFICATION N
AND N.NOTIFICATION_READ_DT IS NULL

/* NotificationRepository.ListNotification */
SELECT N.NOTIFICATION_ID, N.USER_ID, N.NOTIFICATION_TITLE, N.NOTITICATION_CONTENT, N.NOTIFICATION_TYPE_CODE, N.NOTIFICATION_KIND_CODE, N.NOTIFICATION_READ_DT, N.CREATE_DT FROM CO_NOTIFICATION N
ORDER BY N.NOTIFICATION_ID DESC

/* NotificationRepository.AddNotification */

INSERT INTO CO_NOTIFICATION (
  USER_ID,
  NOTIFICATION_TITLE,
  NOTITICATION_CONTENT,
  NOTIFICATION_TYPE_CODE,
  NOTIFICATION_KIND_CODE,
  CREATER_ID
)
VALUES (
  @UserId,
  @NotificationTitle,
  @NotiticationContent,
  @NotificationTypeCode,
  @NotificationKindCode,
  @CreaterId
)
;SELECT LAST_INSERT_ID()
