/* PayslipSalaryDetailMapper.AddPayslipSalaryDetail */
INSERT INTO SA_PAYSLIP_SALARY_DETAIL ( PAYSLIP_ID, SALARY_TYPE_CODE, SALARY_AMOUNT_CODE, SALARY_AMOUNT, CREATER_ID ) VALUES
( ?dto.PayslipId, ?dto.SalaryTypeCode, ?dto.SalaryAmountCode, CASE WHEN ?dto.SalaryAmount IS NOT NULL AND ?dto.SalaryAmount != '' THEN CAST(REPLACE(?dto.SalaryAmount, ',', '') AS DOUBLE) ELSE NULL END, ?dto.CreaterId )
